INSERT INTO `dmat`.`levelsrecommendationsmaster` (`currentlevel`, `recommendedlevel` ,`maximumlevel` ,`devopscategoryid`, `recommendations`, `creationdatetime`, `modificationdatetime`, `creationmode`, `status`) VALUES ('1', '2', '5', '1', 'Progressively look at moving the following components into version control -> application source code & configuration files -> Test scripts->Infrastructure related scripts -> All remaining files used to build, test, configure, deploy, run and document the service.# New work to be kept in separate branches and merged atleast bi-weekly.# Build to be triggered manually at frequent intervals.', NOW(), null, 'manual', 'active');
